document.addEventListener("DOMContentLoaded", () => {
  const e = document.querySelector(".form__select"),
    t = e.querySelector(".form__trigger"),
    c = e.querySelectorAll(".form__option"),
    n = document.getElementById("system-type");
  t.addEventListener("click", () => {
    e.classList.toggle("open");
  }),
    c.forEach((c) => {
      c.addEventListener("click", () => {
        const o = c.getAttribute("data-value"),
          s = c.textContent.trim();
        (t.textContent = s),
          t.classList.add("selected"),
          (n.value = o),
          e.classList.remove("open");
      });
    }),
    document.addEventListener("click", (t) => {
      e.contains(t.target) || e.classList.remove("open");
    });
  const o = document.querySelector(".header__burger"),
    s = document.querySelector(".header__menu");
  o &&
    s &&
    o.addEventListener("click", () => {
      o.classList.toggle("active"), s.classList.toggle("active");
    });
});
